import React, { useState } from 'react';
import './PlanStyle.css';

const TripForm = () => {
  const [formData, setFormData] = useState({
    destination: '',
    startDate: '',
    endDate: '',
    tripType: '',
    budget: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/api/recommendations/filter', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      console.log('Filtered destinations:', data); // Log filtered destinations from backend

      // Optionally, you can update state or perform other actions based on the response
    } catch (error) {
      console.error('Error filtering destinations:', error);
      // Handle error (e.g., show an error message)
    }
  };

  return (
    <div className="trip-form-container">
      <h2>Plan Your Next Adventure</h2>
      <p>Get started by filling out the form below:</p>
      <form onSubmit={handleSubmit}>
        <label>
          Destination
          <input
            type="text"
            name="destination"
            placeholder="Enter your Destination"
            value={formData.destination}
            onChange={handleChange}
          />
        </label>
        {/* Other form fields */}
        {/* ... */}
        <button type="submit">Create Trip</button>
      </form>
    </div>
  );
};

export default TripForm;
