import React, { useState } from 'react';
import RecommendationCard from './RecommendationCard';
import './PlanStyle.css';

const TripForm = () => {
  const [formData, setFormData] = useState({
    destination: '',
    startDate: '',
    endDate: '',
    tripType: '',
    budget: '',
  });
  const [recommendations, setRecommendations] = useState([]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const fetchRecommendations = async () => {
    try {
      const response = await fetch('/api/recommendations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error('Failed to fetch recommendations');
      }
      const data = await response.json();
      setRecommendations(data.recommendations); // Assuming the backend returns recommendations
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchRecommendations();
    console.log('Form Data:', formData);
  };

  return (
    <div className="trip-form-container">
      <h2>Plan Your Next Adventure</h2>
      <p>Get started by filling out the form below:</p>
      <form onSubmit={handleSubmit}>
        {/* Your form inputs */}
        <button type="submit">Create Trip</button>
      </form>
      <div className="recommendations">
        {recommendations.map((destination, index) => (
          <RecommendationCard key={index} destination={destination} />
        ))}
      </div>
    </div>
  );
};

export default TripForm;
